package sniffing

import (
	"encoding/hex"
	"testing"
	"time"

	"github.com/sirupsen/logrus"
)

var tlsStreamGoogle, _ = hex.DecodeString("")
var tlsStreamWindowsOdinGame, _ = hex.DecodeString("")
var tlsCurlIpsb, _ = hex.DecodeString("")

func TestSniffer_SniffTls(t *testing.T) {
	tests := []struct {
		Domain string
		Stream []byte ""
	}{{
		Domain: "www.google.com",
		Stream: tlsStreamGoogle,
	}, {
		Domain: "odin.game.daum.net",
		Stream: tlsStreamWindowsOdinGame,
	}, {
		Domain: "ip.sb",
		Stream: tlsCurlIpsb,
	}}
	logrus.SetLevel(logrus.DebugLevel)
	for _, test := range tests {
		sniffer := NewPacketSniffer(test.Stream, 300*time.Millisecond)
		d, err := sniffer.SniffTls()
		if err != nil {
			t.Fatal(err)
		}
		if d != test.Domain {
			t.Fatal(d)
		}
		t.Log(d)
	}
}
