package sniffing

import (
	"bytes"
	"encoding/hex"
	"io"
	"testing"
	"time"

	"github.com/sirupsen/logrus"
)

var tlsStreamGoogle, _ = hex.DecodeString("")
var tlsStreamWindowsOdinGame, _ = hex.DecodeString("")
var tlsCurlIpsb, _ = hex.DecodeString("")
var tlsWebTelegramOrm, _ = hex.DecodeString("")
var tlsWebTelegramOrm2, _ = hex.DecodeString("")

func TestSniffer_SniffTls(t *testing.T) {
	tests := []struct {
		Domain string
		Stream io.Reader
	}{{
		Domain: "www.google.com",
		Stream: bytes.NewReader(tlsStreamGoogle),
	}, {
		Domain: "odin.game.daum.net",
		Stream: bytes.NewReader(tlsStreamWindowsOdinGame),
	}, {
		Domain: "ip.sb",
		Stream: bytes.NewReader(tlsCurlIpsb),
	}, {
		Domain: "web.telegram.org",
		Stream: io.MultiReader(bytes.NewReader(tlsWebTelegramOrm), bytes.NewReader(tlsWebTelegramOrm2)),
	}}
	logrus.SetLevel(logrus.DebugLevel)
	for _, test := range tests {
		sniffer := NewStreamSniffer(test.Stream, 300*time.Millisecond)
		d, err := sniffer.SniffTcp()
		if err != nil {
			t.Fatal(err)
		}
		if d != test.Domain {
			t.Fatal(d)
		}
		t.Log(d)
	}
}
